Course.create!([
  {title: "Beginner Coder", lead: "Get a taste for web design and Rails coding.", intro: "In an intense three week course, learn basic programming concepts, web design fundamentals, build professional sites with front-end frameworks and discover the power of rapid application development with Rails.", description: "<ul class=\"plus\">\r\n<li>UX & UI design with</li>\r\n<li>HTML5 +  CSS3 +  jQuery</li>\r\n<li>Beautify your apps with front-end frameworks</li>\r\n<li>Object Oriented Programming with Ruby</li>\r\n<li>Rapid application development with Ruby on Rails</li>\r\n<li>Build a personal web app with your own blog</li>\r\n<li>Learn to make use of free, open source software</li>\r\n</ul>", designed_for: "Beginner programmers or those needing a refresher before taking on the more advanced courses.", price: 395, duration: "3 Weeks", active: true, priority: 2, slug: "beginner-coder"},
  {title: "Rails Coder", lead: "Become a confident web developer and build any app you want.", intro: "In just 8 weeks you will be able to build advanced web apps. You will be able to build your own Facebook if you want.", description: "<ul class=\"plus\">\r\n<li>Database design</li>\r\n<li>Application design</li>\r\n<li>Advanced Ruby on Rails techniques</li>\r\n<li>Publishing your app to the web</li>\r\n<li>Use cool AJAX techniques for an interactive user experience</li>\r\n<li>In-app messaging</li>\r\n<li>Optimisation & Analytics</li>\r\n<li>Launching your app to the world</li>\r\n<li>Build a social networking app</li>\r\n<li>Build your own app idea!</li>\r\n</ul>", designed_for: "", price: 1950, duration: "8 weeks", active: true, priority: 4, slug: "rails-coder"},
  {title: "Angular Coder", lead: "Learn the hottest new javascript web development framework -- Angular.js. ", intro: "Angular.js helps you build amazingly responsive and dynamic apps very quickly. Plug it into your Rails backend and you're on a winner!", description: "<ul class=\"plus\">\r\n<li>Angular.js</li>\r\n<li>Advanced front-end design</li>\r\n<li>Creating amazing user experiences</li>\r\n<li>Adding your Angular front end to your Rails back end</li>\r\n<li>Create dynamic and exciting apps</li>\r\n<li>Build your own app idea</li>\r\n<li>Launch your app to the world</li>\r\n</ul>", designed_for: "", price: 950, duration: "4 weeks", active: true, priority: 6, slug: "angular-coder"},
  {title: "Expert Coder", lead: "Learn it all! Web design + Rails app development + Angular.js", intro: "If you want to create amazing apps that blow people away. This is the course for you.", description: "<ul class=\"plus\"> <li>UX & UI design with</li> <li>HTML5 + CSS3 + jQuery</li> <li>Beautify your apps with front-end frameworks</li> <li>Object Oriented Programming with Ruby</li> <li>Rapid application development with Ruby on Rails</li> <li>Build a personal web app with your own blog</li> <li>Learn to make use of free, open source software</li><li>Database design</li> <li>Application design</li> <li>Advanced Ruby on Rails techniques</li> <li>Publishing your app to the web</li> <li>Use cool AJAX techniques for an interactive user experience</li> <li>In-app messaging</li> <li>Optimisation & Analytics</li> <li>Launching your app to the world</li> <li>Build a social networking app</li> <li>Build your own app idea!</li><li>Angular.js</li> <li>Advanced front-end design</li> <li>Creating amazing user experiences</li> <li>Adding your Angular front end to your Rails back end</li> <li>Create dynamic and exciting apps</li> </ul>", designed_for: "", price: 3250, duration: "15 weeks", active: true, priority: 8, slug: "expert-coder"},
  {title: "Summer Coder Camp - Rails + Angular", lead: "Learn Ruby on Rails and Angular.js in this intensive full time course.", intro: "Now is the time to build that app you've been thinking about for ages. Learn to build it yourself and launch it in just 6 weeks. Everything from the Expert Coder course but with more time to dedicate to building your app with ongoing support for life.", description: "<ul class=\"plus\"> <li>UX & UI design with</li> <li>HTML5 + CSS3 + jQuery</li> <li>Beautify your apps with front-end frameworks</li> <li>Object Oriented Programming with Ruby</li> <li>Rapid application development with Ruby on Rails</li> <li>Build a personal web app with your own blog</li> <li>Learn to make use of free, open source software</li><li>Database design</li> <li>Application design</li> <li>Advanced Ruby on Rails techniques</li> <li>Publishing your app to the web</li> <li>Use cool AJAX techniques for an interactive user experience</li> <li>In-app messaging</li> <li>Optimisation & Analytics</li> <li>Launching your app to the world</li> <li>Build a social networking app</li> <li>Build your own app idea!</li><li>Angular.js</li> <li>Advanced front-end design</li> <li>Creating amazing user experiences</li> <li>Adding your Angular front end to your Rails back end</li> <li>Create dynamic and exciting apps</li> </ul>", designed_for: "", price: 4800, duration: "6 weeks", active: true, priority: 10, slug: "summer-coder-camp-rails-angular"}
])
